mapper.update:

update board 
   set o_no = (o_no + 1) 
 where g_no= #{g_no} and o_no > #{o_no}


update board 
   set title = #{title}
	 , contents = #{contents}
	 , state = 'u'
 where no = #{no}


update board 
   set state = 'n' 
 where no = #{no}


update board 
   set hit =  #{hit} 
 where no = #{no}

mapper.select:

select hit
  from board 
 where no = #{no}
